// src/redux/reducers/configureStore.js
import { configureStore } from '@reduxjs/toolkit';
import rootReducer from './rootReducer';  // adjust path accordingly

const store = configureStore({
  reducer: rootReducer,
  // middleware: (getDefaultMiddleware) => getDefaultMiddleware().concat(/* other middleware like thunk if needed */)
});

export default store;
